columns.handlerTsCode:
(line: Message): CellHandler => {
    const ts = line.json_content?.time;
    if (!ts) return { text: '-' };
    const d = new Date(ts);
    return { text: d.toTimeString().slice(0, 8) };
}

(line: Message): CellHandler => {
    const level = line.json_content?.level;
    const config = {
        20: { text: 'DEBUG', style: { color: '#888' } },
        30: { text: 'INFO', style: { color: '#4ade80' } },
        40: { text: 'WARN', style: { color: '#facc15', fontWeight: 'bold' } },
        50: { text: 'ERROR', style: { color: '#f87171', fontWeight: 'bold' } },
        60: { text: 'FATAL', style: { color: '#f87171', fontWeight: 'bold' } }
    };
    return config[level] || { text: '???', style: { color: '#888' } };
}

(line: Message): CellHandler => {
    const service = line.json_content?.service;
    return { text: service ?? "-" }
}

(line: Message): CellHandler => {
  const err = line.json_content?.error?.message;
  return { text: err || '-', style: err ? { color: '#f87171' } : {} };
}

middlewares.handlerTsCode:
(line: Message): Message | void => {
  const level = line.json_content?.level;
  const msg = line.json_content?.msg;

  // Filter debug logs
  if (level < 30) return;

  // Filter noisy logs
  if (!line.content || line.content.includes('(compile:')) return;

  return line;
}
